	GET


<set-variable name="snlClient" value="@{
		var secret = ((IResponse)context.Variables[" snlClientResponse"]).Body.As();
		return secret[" value"].ToString();
}" />

	https://#{keyVaultHost}#/secrets/snl-user-pwd/?api-version=7.0
	GET


<set-variable name="snlSecret" value="@{
		var secret = ((IResponse)context.Variables[" snlSecretResponse"]).Body.As();
		return secret[" value"].ToString();
}" />

